Chartist.plugins = Chartist.plugins || {};
var euro = function(x) {
    return '' + x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ',');
};
Chartist.plugins.ctBarLabels = function (options) {

    options = Chartist.extend({}, {}, options);

    return function ctBarLabels(chart) {
      if (chart instanceof Chartist.Bar) {
        chart.on('draw', function (data) {
          var barHorizontalCenter, barVerticalCenter, label, value;
  if (data.type === "bar") {
    barHorizontalCenter = data.x1 + (data.element.width() * .2);
    barVerticalCenter   = data.y1 + (data.element.height() * -1) - 10;
    value               = data.element.attr('ct:value');
    var ccvt            = (typeof options.convert == "undefined") ? function(x){return x; } : options.convert;
    if (value !== '0') {
      label = new Chartist.Svg('text');
      label.text(ccvt(value));
      label.addClass("ct-barlabel");
      label.attr({
        x: barHorizontalCenter,
        y: barVerticalCenter,
        'text-anchor': 'middle'
      });
      return data.group.append(label);
    }
}
        });
      }
    };
  };
